import React, { useState } from "react";
import { Link } from "react-router-dom";  // Import Link for navigation

const SideBar = () => {
  const [isVisible, setIsVisible] = useState(false);

  const handleMouseEnter = () => {
    setIsVisible(true);  // Show sidebar when mouse enters the left side
  };

  const handleMouseLeave = () => {
    setIsVisible(false);  // Hide sidebar when mouse leaves the left side
  };

  return (
    <div 
      className={`sidebar ${isVisible ? "visible" : ""}`}
      onMouseEnter={handleMouseEnter}  // Show sidebar when mouse enters
      onMouseLeave={handleMouseLeave}  // Hide sidebar when mouse leaves
    >
      <ul>
        <li>
          <Link to="/">Home</Link> {/* Link to the Main screen */}
        </li>
        <li>
          <Link to="/plans">Plans</Link>  {/* Link to the Plans screen */}
        </li>
        <li>
          <Link to="/about">About</Link>  {/* Link to the About screen */}
        </li>
        <li>
          <Link to="/contact">Contact</Link>  {/* Link to the Contact screen */}
        </li>
      </ul>
    </div>
  );
};

export default SideBar;
