import React, { useState } from "react";
import { FaPlusCircle,FaMinusCircle } from "react-icons/fa";
import './Plans.css'; // Importing CSS for styling

const Plans = () => {
  const [planList, addPlan] = useState([]);
  const [planInput1, setPlanInput1] = useState("");
  const [planInput2, setPlanInput2] = useState("");
  const [planInput3, setPlanInput3] = useState("");
  const [planInput4, setPlanInput4] = useState("");
  const [InputDisabled, setInputDisabled] = useState(false); // State to manage input field disabled status]
  const [plan,setPlanTitle] = useState(''); // State to manage plan title
  const [taskList, setTaskList] = useState([]); // State to manage task list
  // Handle adding the plan when the button is clicked
  const changePlanStatus = () => {
    if (planInput.trim() !== "") {
      addPlan([...planList, planInput]); // Add the current input value to the planList array
      setPlanInput(""); // Clear the input field after adding the plan
      setInputDisabled(true); // Disable the input field after adding a plan
    }
  };

  class Plan{
    constructor(PlanTitle) {
      this.planTitle = PlanTitle;
      this.content = []; // Initially set as not completed
    }
  }


  return (
    <div className="Plans">
      <div className="Plan-Title">
        <h1>Plans Screen</h1>
      </div>

      <div className="Plans-Container">
        {/* First Plan */}
        <div className="First-Plan">
          <div className="Plans-Content">
            {/* Input field for Plan #1 */}
            <input
              type="text"
              placeholder="Plan #1"
              value={planInput1} // Bind the input value to the planInput state
              onChange={(e) => setPlanInput1(e.target.value)} // Update planInput state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }} // Hide the input field if InputDisabled is true
            />
            {/* Button to add plan */}
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={changePlanStatus} // Add the plan to the list when clicked
            >{InputDisabled ? (
              <FaMinusCircle /> // Icon when input is disabled
            ) : (
              <FaPlusCircle /> // Icon when input is enabled
            )}
            </button>
            {/* Display the first plan from the list */}
            <span style={{left:"-240px", position:"relative", marginRight:"10px"}}>{planList[0]}</span>
          </div>
        </div>

        {/* Second Plan */}
        <div className="Second-Plan">
          <div className="Plans-Content">
          <input
              type="text"
              placeholder="Plan #2"
              value={planInput2} // Bind the input value to the planInput state
              onChange={(e) => setPlanInput2(e.target.value)} // Update planInput state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }} // Hide the input field if InputDisabled is true
            />
            {/* Button to add plan */}
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={changePlanStatus} // Add the plan to the list when clicked
            >{InputDisabled ? (
              <FaMinusCircle /> // Icon when input is disabled
            ) : (
              <FaPlusCircle /> // Icon when input is enabled
            )}
            </button>
            {/* Display the first plan from the list */}
            <span style={{left:"-240px", position:"relative", marginRight:"10px"}}>{planList[0]}</span>
          </div>
        </div>

        {/* Third Plan */}
        <div className="Third-Plan">
          <div className="Plans-Content">
          <input
              type="text"
              placeholder="Plan #2"
              value={planInput3} // Bind the input value to the planInput state
              onChange={(e) => setPlanInput3(e.target.value)} // Update planInput state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }} // Hide the input field if InputDisabled is true
            />
            {/* Button to add plan */}
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={changePlanStatus} // Add the plan to the list when clicked
            >{InputDisabled ? (
              <FaMinusCircle /> // Icon when input is disabled
            ) : (
              <FaPlusCircle /> // Icon when input is enabled
            )}
            </button>
            {/* Display the first plan from the list */}
            <span style={{left:"-240px", position:"relative", marginRight:"10px"}}>{planList[0]}</span>
          </div>
        </div>

        {/* Fourth Plan */}
        <div className="Fourth-Plan">
          <div className="Plans-Content">
          <input
              type="text"
              placeholder="Plan #2"
              value={planInput4} // Bind the input value to the planInput state
              onChange={(e) => setPlanInput4(e.target.value)} // Update planInput state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }} // Hide the input field if InputDisabled is true
            />
            {/* Button to add plan */}
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={changePlanStatus} // Add the plan to the list when clicked
            >{InputDisabled ? (
              <FaMinusCircle /> // Icon when input is disabled
            ) : (
              <FaPlusCircle /> // Icon when input is enabled
            )}
            </button>
            {/* Display the first plan from the list */}
            <span style={{left:"-240px", position:"relative", marginRight:"10px"}}>{planList[0]}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Plans;
