import React, { useEffect, useState } from "react";
import './App.css';
import './index.css';
import { isVisible } from "@testing-library/user-event/dist/utils";
import SideBar from "./SideBar";

function App() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const SideBar =() => {
    const [isVisible, setIsVisible] = useState(false);
    const HanldeMouseOver = (e) => {
      if (e.clientX <=50) {
        setIsVisible(true);
      }else{
        setIsVisible(false);
      }
    };
  }
  useEffect(() => {{
    window.addEventListener("mousemove", handleMouseOver);
    return () => {
      window.removeEventListener("mousemove", handleMouseOver);
    };
  }}, []);

  // TaskNode class moved outside of the App function
  class TaskNode {
    constructor(description) {
      this.description = description;
      this.completed = false;  // Initially set as not completed
    }
  }

  const getInput = (event) => {
    setInputValue(event.target.value);
  };

  const addTask = () => {
    if (inputValue.trim() !== "") {
      const newTask = new TaskNode(inputValue);
      setTasks([...tasks, newTask]);
      setInputValue("");  // Clear the input field
    }
  };


  const getTasks = () => {
    return tasks.map((task, index) => (
      <div className="List-Container" key={index}>
      <li key={index}>
        <div className="Space-between-tasks">
  <input
    type="checkbox"
    checked={task.completed}  // Bind checkbox state to task completion
    onChange={() => toggleTaskCompletion(index)} // Toggle task completion on checkbox click
  />
  <span id="Task-text">{task.description}</span>
  </div>
</li>
</div>
    ));
  };
  const toggleTaskCompletion = (index) => {

    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };
  return (
    <div className="App">
      <div className="Navigation">
        <div className={`SideBar ${isVisible ? "visible" : '' }`}>
    <ul>
          <li><a href="#">Home</a></li>
          <li><a href="#">About</a></li>
          <li><a href="#">Services</a></li>
          <li><a href="#">Contact</a></li>
    </ul>
        </div>
        <input
          type="text"
          value={inputValue}
          onChange={getInput}
          placeholder="Enter a task"
        />
        <button id="Task-Function" onClick={addTask}>
          Add Task
        </button>
      </div>

      <div className="container">
        <div className="Task">
          <ul>{getTasks()}</ul>
        </div>
      </div>
    </div>
  );
}

export default App;
