import React, { useState } from "react";
import './App.css';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");

  // TaskNode class moved outside of the App function
  class TaskNode {
    constructor(description) {
      this.description = description;
      this.completed = false;
    }
  }

  const getInput = (event) => {
    setInputValue(event.target.value);
  };

  const addTask = () => {
    if (inputValue.trim() !== "") {
      const newTask = new TaskNode(inputValue);
      setTasks([...tasks, newTask]);
      setInputValue("");
    }
  };
  const handleCheckboxChange = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;  // Toggle the completed state
    setTasks(updatedTasks);
  };

  const getTasks = () => {
    return tasks.map((task, index) => (
      <li key={index} className="task-item">
        <input 
          type="checkbox"
          className="Check"
          checked={task.completed}
          onChange={() => handleCheckboxChange(index)} // Toggle checkbox state
        />
        <span>{task.description}</span>
      </li>
    ));
  };

  return (
    <div className="App">
      <div className="Navigation">
        <h1 className="title">To-Do List</h1>
        <input
          type="text"
          value={inputValue}
          onChange={getInput} // Update inputValue state as user types
          placeholder="Enter a task"
        />
        <button id="Task-Function" onClick={addTask}>
          Add Task
        </button>
      </div>

      <div className="container">
        <div className="Task">
          <ul>{getTasks()}</ul> {/* Render the tasks list */}
        </div>
      </div>
    </div>
  );
}

export default App;
