import React, { useState, useEffect } from "react";
import { FaPlusCircle, FaMinusCircle } from "react-icons/fa";
import "./Plans.css";

// Plan class that holds tasks
class Plan {
  constructor(name, description) {
    this.name = name;
    this.taskList = [description]; // Initialize with the description passed to the constructor
  }

  // Method to add more tasks to the taskList
  addTask(description) {
    this.taskList.push(description); // Add a new task to the taskList
  }

  // Method to get the task list
  getTasks() {
    return this.taskList;
  }
}

const Plans = () => {
  // Initialize planList from localStorage (or empty array if not found)
  const [planList, setPlanList] = useState(() => {
    const saved = localStorage.getItem("plans");
    // Re-initialize each plan in planList to be an instance of Plan class
    return saved ? JSON.parse(saved).map(plans => new Plan(plans.name, plans.taskList[0])) : [];
  });

  // State variables for each plan's input (planInput1, planInput2, etc.)
  const [planInput1, setPlanInput1] = useState("");
  const [planInput2, setPlanInput2] = useState("");
  const [planInput3, setPlanInput3] = useState("");
  const [planInput4, setPlanInput4] = useState("");

  // Persist planList in localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("plans", JSON.stringify(planList));
  }, [planList]);

  // Helper function to add a new plan object directly to the state
  const addPlan = (index, inputValue, setter) => {
    if (!inputValue.trim()) return;

    // Create a new Plan object with the input value as description directly using the constructor
    const newPlan = new Plan(`Plan ${index }`, inputValue);

    // Create a new array of plans with the new plan added
    setPlanList((prevList) => {
      const updatedList = [...prevList];
      updatedList[index] = newPlan; // Update the plan at the given index
      return updatedList;
    });

    setter(""); // Clear the input field after adding the task
  };

  // Remove the plan at a specific index
  const clearPlan = (index) => {
    setPlanList((prevList) => {
      const updatedList = [...prevList];
      updatedList.splice(index, 1); // Remove the plan at the given index
      return updatedList;
    });
  };

  // Helper to render one slot with tasks
  const renderSlot = (index, inputValue, setter) => {
    const hasPlan = planList[index] != null;
    return (
      <div className={`Plan-${index + 1}`}>
        <div className="Plans-Content">
          {hasPlan ? (
            <>
              <h3>{planList[index].name}</h3>
              <ul>
                {planList[index].getTasks().map((task, i) => (
                  <li key={i}>{task}</li>
                ))}
              </ul>
            </>
          ) : (
            <input
              type="text"
              placeholder={`Plan #${index + 1}`}
              value={inputValue}
              onChange={(e) => setter(e.target.value)}
            />
          )}
          <button
            className="Add-Plan"
            onClick={() =>
              hasPlan
                ? clearPlan(index)
                : addPlan(index, inputValue, setter)
            }
            style={{
              border: "none",
              cursor: "pointer",
              background: "none",
            }}
          >
            {hasPlan ? <FaMinusCircle /> : <FaPlusCircle />}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="Plans">
      <div className="Plan-Title">
        <h1>Plans Screen</h1>
      </div>
      <div className="Plans-Container">
        {renderSlot(0, planInput1, setPlanInput1)}
        {renderSlot(1, planInput2, setPlanInput2)}
        {renderSlot(2, planInput3, setPlanInput3)}
        {renderSlot(3, planInput4, setPlanInput4)}
      </div>
    </div>
  );
};

export default Plans;
