import React, { useState } from "react";
import './App.css';
import './index.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");

  // TaskNode class moved outside of the App function
  class TaskNode {
    constructor(description) {
      this.description = description;
      this.completed = false;  // Initially set as not completed
    }
  }

  const getInput = (event) => {
    setInputValue(event.target.value);
  };

  const addTask = () => {
    if (inputValue.trim() !== "") {
      const newTask = new TaskNode(inputValue);
      setTasks([...tasks, newTask]);
      setInputValue("");  // Clear the input field
    }
  };

  // Function to handle checkbox toggle
  const toggleTaskCompletion = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);  // Update the state
  };

  const getTasks = () => {
    return tasks.map((task, index) => (
      <li key={index}>
        {/* Changed from <button> to <input type="checkbox"> */}
        <input
          type="checkbox"
          checked={task.completed}  // Bind checkbox state to task completion
          onChange={() => toggleTaskCompletion(index)} // Toggle task completion
        />
        <span>{task.description}</span>
      </li>
    ));
  };

  return (
    <div className="App">
      <div className="Navigation">
        <input
          type="text"
          value={inputValue}
          onChange={getInput}
          placeholder="Enter a task"
        />
        <button id="Task-Function" onClick={addTask}>
          Add Task
        </button>
      </div>

      <div className="container">
        <div className="Task">
          <ul>{getTasks()}</ul>
        </div>
      </div>
    </div>
  );
}

export default App;
