import React, { useState } from "react";
import './App.css';  // Make sure this is in App.js
import './index.css';  // Make sure this is in index.js


function App() {
 

  const [tasks,setTasks] = useState([]);
  class TaskNode{
      constructor(description){
          this.description=description;
          this.completed=false;
          this.next=null;
      }
  }
  class TaskList{
      constructor(){
          this.head=null;
      }
      addTask(description){
          const newTask = new TaskNode(description);
          if(this.head === null){
              this.head = newTask;
          }else{
              let current = this.head;
              while(current.next !== null){
                  current = current.next;
              }
              current.next = newTask;
          }
      }
  
      getTasks() {
          let current = this.head;
          let tasks = [];
          while (current) {
              tasks.push({ description: current.description, completed: current.completed });
              current = current.next;
          }
          return tasks;
      }
  }
  const taskList= new TaskList();
  const addTask = (description) => {
    taskList.addTask(description);  // Add task to the task list
    setTasks(taskList.getTasks());  // Update state to re-render the task list
  };

  return (
    <div className="App">
      <div className="Navigation">
        <button
          id="Task-Function"
          onClick={() =>addTask('New Task')} // Adding a task when the button is clicked
        >
          Add Task
        </button>
      </div>

      <div className="container">
        <div className="Task">
          {/* Render tasks */}
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>
                <span>{task.description}</span>
                {/* You can add more functionalities like a checkbox or remove button */}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
