import React, { useState, useEffect,useRef, use } from 'react';
const Sidebar = () => {
  const [isVisible, setIsVisible] = useState(false);
  const sidebarRef =useRef(null);
  // Function to handle mouse movement
  const handleMouseMove = (e) => {
    const sidebarWidth = sidebarRef.current ? sidebarRef.current.offsetWidth:0; // Get the width of the sidebar
    if (e.clientX <= sidebarWidth) {
      setIsVisible(true); // Show sidebar when mouse is near the left edge (within 50px)
    } else {
      setIsVisible(false); // Hide sidebar when mouse moves away from the left edge
    }
  };

  useEffect(() => {
    // Attach mousemove event listener when the component mounts
    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup event listener when the component unmounts
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div>
      
      <div ref={sidebarRef} className={`sidebar ${isVisible ? 'visible' : ''}`}>
        <ul>
          <li><a href="#" type="button">Plans</a></li>
          <li><a href="#">About</a></li>
          <li><a href="#">Contact</a></li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
