        {planList.map((plan, i) => (
          <li key={i}>{plan}</li>
        ))}
      </ul>
      </div>

      <div className="Navigation">
        <input
          type="text"
          value={inputValue}
          onChange={getInput}
          placeholder="Enter a task"
        />
        <button id="Task-Function" onClick={addTask}>
          Add Task
        </button>
      </div>

      <div className="container">
        <div className="Task">
          <ul>{getTasks()}</ul>
        </div>
      </div>

      
    

    
    </div>
  );
};

export default Task;
