import React, { useState } from "react";
import './App.css';  // Make sure this is in App.js
import './index.css';  // Make sure this is in index.js


function App() {
 

  const [tasks,setTasks] = useState([]);
  class TaskNode{
      constructor(description){
          this.description=description;
          this.completed=false;
          this.next=null;
      }
  }
  class TaskList{
      constructor(){
          this.head=null;
      }
      addTask(description){
          const newTask = new TaskNode(description);
          if(this.head === null){
              this.head = newTask;
          }else{
              let current = this.head;
              while(current.next !== null){
                  current = current.next;
              }
              current.next = newTask;
          }
      }
  
      getTasks() {
          let current = this.head;
          let tasks = [];
          while (current) {
              tasks.push({ description: current.description, completed: current.completed });
              current = current.next;
          }
          return tasks;
      }
  }
  return (
<div className="App">
    <div class="Navigation">
   
      <button id="Task-Function" >Add Task</button>
      <button id="Task-Function">Remove Task</button>
      <button id="Task-Function">Edit Task</button>
   
      </div>
  
  <div class="container">
<div>Tasks</div>
    <div class="Task" getTasks={tasks}>
    </div>

  </div>
</div>
  );
}

export default App;
