import React from "react";
import SideBar from "./SideBar";
import './Plans.css'; // Importing CSS for styling
import { FaPlus } from "react-icons/fa";
import {FaPlusCircle} from "react-icons/fa";
import { useState } from "react";
const Plans = () => {
  
const [planList, addPlan] = useState([]);
const [planInput, setPlanInput] = useState("");
// let planList = []; // Removed redundant declaration
const changePlanStatus = (event) => {
  if (planInput.trim() !== "") {
    // Add the current input value to the planList array
    addPlan([...planList, planInput]);
    setPlanInput(""); // Clear the input field after adding the plan
  }
};

  return (
    <div className="Plans">
<div className="Plan-Title">

<h1 >Plans Screen</h1>
<span style={{display:"flex",alignItems:"center", position:"relative", top:"118px", left:"300px"}}>
</span>
</div>
      <div className="Plans-Container">
      
      <div className="First-Plan" style={{marginTop:"0px"}}>

      <div className="Plans-Content">
      <input type="text" placeholder="Plan #1"/>
      <button className="Add-Plan" style={{border:"none",cursor:"pointer", background:"none", position:"relative", top:"3px"}} value={planInput} onChange={(e) =>  setPlanInput(e.target.value)}><FaPlusCircle/></button>
      <span>{planList[0]}</span>
      </div>

      </div>

      <div className="Second-Plan" >

<div className="Plans-Content">
<input type="text" placeholder="Plan #2"/>
<button className="Add-Plan" style={{border:"none",cursor:"pointer", background:"none", position:"relative", top:"3px"}}><FaPlusCircle/></button>
</div>

</div>
<div className="Third-Plan" >

      <div className="Plans-Content">
      <input type="text" placeholder="Plan #3"/>
      <button className="Add-Plan" style={{border:"none",cursor:"pointer", background:"none", position:"relative", top:"3px"}}><FaPlusCircle/></button>
      </div>

      </div>
      <div className="Fourth-Plan">

      <div className="Plans-Content">
      <input type="text" placeholder="Plan #4"/>
      <button className="Add-Plan" style={{border:"none",cursor:"pointer", background:"none", position:"relative", top:"3px"}}><FaPlusCircle/></button>
      </div>

      </div>
      

      </div>
    </div>
  );
};

export default Plans;
