import React, { useState, useEffect } from "react";
import { FaPlusCircle, FaMinusCircle } from "react-icons/fa";
import './Plans.css'; // Importing CSS for styling

const Plans = () => {
  const [planList, setPlanList] = useState([]);
  const [planInput1, setPlanInput1] = useState("");
  const [planInput2, setPlanInput2] = useState("");
  const [planInput3, setPlanInput3] = useState("");
  const [planInput4, setPlanInput4] = useState("");
  const [InputDisabled1, setInputDisabled1] = useState(false); 
  const [InputDisabled2, setInputDisabled2] = useState(false); 
  const [InputDisabled3, setInputDisabled3] = useState(false); 
  const [InputDisabled4, setInputDisabled4] = useState(false); 

  // Handle adding the plan when the button is clicked
  const changePlanStatus = (planInput, planSetter, setInputDisabled) => {
    if (typeof planInput === "string" && planInput.trim() !== "") {
      const updatedPlanList = [...planList, planInput]; // Add the current input value to the planList array
      setPlanList(updatedPlanList); // Update the planList state
      planSetter(""); // Clear the input field after adding the plan
      setInputDisabled(true); // Disable the input field after adding a plan
    }
  };

  // Retrieve saved plan list from localStorage on component mount
  useEffect(() => {
    const savedPlans = JSON.parse(localStorage.getItem("plans"));
    if (savedPlans) {
      setPlanList(savedPlans); // Set saved plans into the state
    }
  }, []);

  // Save plan list to localStorage whenever it changes
  useEffect(() => {
    if (planList.length > 0) {
      localStorage.setItem("plans", JSON.stringify(planList)); // Save the updated planList to localStorage
    }
  }, [planList]);

  // Handle clearing the plan and re-enabling the input field
  const clearPlan = (planSetter, setInputDisabled) => {
    planSetter(""); // Clear the input field
    setInputDisabled(false); // Re-enable the input field
  };

  return (
    <div className="Plans">
      <div className="Plan-Title">
        <h1>Plans Screen</h1>
      </div>

      <div className="Plans-Container">
        {/* First Plan */}
        <div className="First-Plan">
          <div className="Plans-Content">
            {!InputDisabled1 ? (
              <input
                type="text"
                placeholder="Plan #1"
                value={planInput1}
                onChange={(e) => setPlanInput1(e.target.value)}
              />
            ) : (
              <span>{planList[0]}</span> // Display plan if input is disabled
            )}
            <button
              className="Add-Plan"
              style={{ border: "none", cursor: "pointer", background: "none", position: "relative", top: "3px" }}
              onClick={() => changePlanStatus(planInput1, setPlanInput1, setInputDisabled1)}
            >
              
            </button>
            {/* Button to clear plan and re-enable input */}
            {InputDisabled1 && (
              <button
                className="Clear-Plan"
                onClick={() => clearPlan(setPlanInput1, setInputDisabled1)}
                style={{backgroundColor:"white", border:"0px", width:"fit-content", height:"fit-content",he}}
              >
                <FaMinusCircle />
              </button>
            )}
          </div>
        </div>

        {/* Second Plan */}
        <div className="Second-Plan">
          <div className="Plans-Content">
            {!InputDisabled2 ? (
              <input
                type="text"
                placeholder="Plan #2"
                value={planInput2}
                onChange={(e) => setPlanInput2(e.target.value)}
              />
            ) : (
              <span>{planList[1]}</span> // Display plan if input is disabled
            )}
            <button
              className="Add-Plan"
              style={{ border: "none", cursor: "pointer", background: "none", position: "relative", top: "3px" }}
              onClick={() => changePlanStatus(planInput2, setPlanInput2, setInputDisabled2)}
            >
              {InputDisabled2 ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            {/* Button to clear plan and re-enable input */}
            {InputDisabled2 && (
              <button
                className="Clear-Plan"
                onClick={() => clearPlan(setPlanInput2, setInputDisabled2)}
              >
                <FaMinusCircle />
              </button>
            )}
          </div>
        </div>

        {/* Third Plan */}
        <div className="Third-Plan">
          <div className="Plans-Content">
            {!InputDisabled3 ? (
              <input
                type="text"
                placeholder="Plan #3"
                value={planInput3}
                onChange={(e) => setPlanInput3(e.target.value)}
              />
            ) : (
              <span>{planList[2]}</span> // Display plan if input is disabled
            )}
            <button
              className="Add-Plan"
              style={{ border: "none", cursor: "pointer", background: "none", position: "relative", top: "3px" }}
              onClick={() => changePlanStatus(planInput3, setPlanInput3, setInputDisabled3)}
            >
              {InputDisabled3 ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            {/* Button to clear plan and re-enable input */}
            {InputDisabled3 && (
              <button
                className="Clear-Plan"
                onClick={() => clearPlan(setPlanInput3, setInputDisabled3)}
              >
                <FaMinusCircle />
              </button>
            )}
          </div>
        </div>

        {/* Fourth Plan */}
        <div className="Fourth-Plan">
          <div className="Plans-Content">
            {!InputDisabled4 ? (
              <input
                type="text"
                placeholder="Plan #4"
                value={planInput4}
                onChange={(e) => setPlanInput4(e.target.value)}
              />
            ) : (
              <span>{planList[3]}</span> // Display plan if input is disabled
            )}
            <button
              className="Add-Plan"
              style={{ border: "none", cursor: "pointer", background: "none", position: "relative", top: "3px" }}
              onClick={() => changePlanStatus(planInput4, setPlanInput4, setInputDisabled4)}
            >
              {InputDisabled4 ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            {/* Button to clear plan and re-enable input */}
            {InputDisabled4 && (
              <button
                className="Clear-Plan"
                onClick={() => clearPlan(setPlanInput4, setInputDisabled4)}
              >
               <FaMinusCircle />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Plans;
