import React, { useState } from "react";
import "./Task.css";

export default function Task({ planList }) {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [selectedPlan, setSelectedPlan] = useState(planList[0] || "");

  // Handle input change
  const handleInput = (e) => setInputValue(e.target.value);

  // Add a new task under the selected plan
  const addTask = () => {
    if (!inputValue.trim()) return;
    const newTask = {
      description: inputValue.trim(),
      completed: false,
      plan: selectedPlan,
    };
    setTasks((prev) => [...prev, newTask]);
    setInputValue("");
  };

  // Toggle completion status
  const toggleTask = (index) => {
    setTasks((prev) => {
      const updated = [...prev];
      updated[index].completed = !updated[index].completed;
      return updated;
    });
  };

  // Only display tasks for the selected plan
  const filteredTasks = tasks.filter((t) => t.plan === selectedPlan);

  return (
    <div className="Task">
      <h1 className="title">To-Do List</h1>

      <div className="Plan-Navigation">
        {planList.map((plan, i) => (
          <button
            key={i}
            className={plan === selectedPlan ? "active" : ""}
            onClick={() => setSelectedPlan(plan)}
          >
            {plan}
          </button>
        ))}
      </div>

      <div className="Navigation">
        <input
          type="text"
          value={inputValue}
          onChange={handleInput}
          placeholder="Enter a task"
        />
        <button id="Task-Function" onClick={addTask}>
          Add Task
        </button>
      </div>

      <div className="container">
        <ul>
          {filteredTasks.map((task, index) => (
            <li key={index} className="List-Container">
              <div className="Space-between-tasks">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(tasks.indexOf(task))}
                />
                <span id="Task-text">{task.description}</span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
