import React from "react";
import { Link } from 'react-router-dom';  // Import Link from react-router-dom
import { useState } from 'react';  // Import useState from React
const Sidebar = () => {

  const [isVisible, setIsVisible] = useState(false);
  const handleMouseOver =() => {
    setIsVisible(true);  // Show the sidebar when mouse is over
  }
  const handleMouseOut =() => {
    setIsVisible(false);  // Hide the sidebar when mouse is out
  }


  return (
    <div className={`sidebar ${isVisible ? 'visible' : ''}`}>
      <ul>
        <li>
          <Link to="/">Home</Link> {/* Link to the Main screen */}
        </li>
        <li>
          <Link to="/plans">Plans</Link>  {/* Link to the Plans screen */}
        </li>
        <li>
          <Link to="/about">About</Link>  {/* Link to the About screen */}
        </li>
        <li>
          <Link to="/contact">Contact</Link>  {/* Link to the Contact screen */}
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
