import React, { useState } from "react";
import { FaPlusCircle, FaMinusCircle } from "react-icons/fa";
import './Plans.css'; // Importing CSS for styling

const Plans = () => {
  const [planList, setPlanList] = useState([]);
  const [planInput1, setPlanInput1] = useState("");
  const [planInput2, setPlanInput2] = useState("");
  const [planInput3, setPlanInput3] = useState("");
  const [planInput4, setPlanInput4] = useState("");
  const [InputDisabled, setInputDisabled] = useState(false); // State to manage input field disabled status
  
  // Handle adding the plan when the button is clicked
  const changePlanStatus = (planInput, planSetter) => {
    if (typeof planInput === "string" && planInput.trim() !== "") {
      setPlanList([...planList, planInput]); // Add the current input value to the planList array
      planSetter(""); // Clear the input field after adding the plan
      setInputDisabled(true); // Disable the input field after adding a plan
    }
  };

  return (
    <div className="Plans">
      <div className="Plan-Title">
        <h1>Plans Screen</h1>
      </div>

      <div className="Plans-Container">
        {/* First Plan */}
        <div className="First-Plan">
          <div className="Plans-Content">
            <input
              type="text"
              placeholder="Plan #1"
              value={planInput1} // Bind the input value to the planInput1 state
              onChange={(e) => setPlanInput1(e.target.value)} // Update planInput1 state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }} // Hide the input field if InputDisabled is true
            />
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={() => changePlanStatus(planInput1, setPlanInput1)} // Pass planInput1 and its setter
            >
              {InputDisabled ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            {/* Display the first plan from the list */}
            <span>{planList[0]}</span>
          </div>
        </div>

        {/* Second Plan */}
        <div className="Second-Plan">
          <div className="Plans-Content">
            <input
              type="text"
              placeholder="Plan #2"
              value={planInput2} // Bind the input value to the planInput2 state
              onChange={(e) => setPlanInput2(e.target.value)} // Update planInput2 state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }}
            />
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={() => changePlanStatus(planInput2, setPlanInput2)} // Pass planInput2 and its setter
            >
              {InputDisabled ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            <span>{planList[1]}</span>
          </div>
        </div>

        {/* Third Plan */}
        <div className="Third-Plan">
          <div className="Plans-Content">
            <input
              type="text"
              placeholder="Plan #3"
              value={planInput3} // Bind the input value to the planInput3 state
              onChange={(e) => setPlanInput3(e.target.value)} // Update planInput3 state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }}
            />
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={() => changePlanStatus(planInput3, setPlanInput3)} // Pass planInput3 and its setter
            >
              {InputDisabled ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            <span>{planList[2]}</span>
          </div>
        </div>

        {/* Fourth Plan */}
        <div className="Fourth-Plan">
          <div className="Plans-Content">
            <input
              type="text"
              placeholder="Plan #4"
              value={planInput4} // Bind the input value to the planInput4 state
              onChange={(e) => setPlanInput4(e.target.value)} // Update planInput4 state on change
              disabled={InputDisabled} // Disable the input field if InputDisabled is true
              style={{ visibility: InputDisabled ? "hidden" : "visible" }}
            />
            <button
              className="Add-Plan"
              style={{
                border: "none",
                cursor: "pointer",
                background: "none",
                position: "relative",
                top: "3px",
              }}
              onClick={() => changePlanStatus(planInput4, setPlanInput4)} // Pass planInput4 and its setter
            >
              {InputDisabled ? <FaMinusCircle /> : <FaPlusCircle />}
            </button>
            <span>{planList[3]}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Plans;
